import React, { Component } from 'react';
import './App.css';
import Clarifai from 'clarifai'
import Particles from 'react-particles-js';
import Navigation from './components/Navigation/Navigation'
import Logo from './components/Logo/Logo'
import Rank from './components/Rank/Rank'
import ImageLinkForm from './components/ImageLinkForm/ImageLinkForm'
import FaceRecognition from './components/FaceRecognition/FaceRecognition'

const app = new Clarifai.App({
  apiKey: '373b18531b924a42be1645f92ea499c7'
});

const particlesOptions = {
  particles: {
    number: {
      value: 80,
      desnity: {
        enable: true,
        value_area: 800

      }
    }
  }
}
class App extends Component {
  constructor() {
    super();
    this.state = {
      input: '',
      imageUrl: ''
    }
  }
  onInputChange = (event) => {
    this.setState({input: event.target.value})
  }

  onButtonSubmit = () => {
    console.log('click')
    this.setState({imageUrl: this.state.input})
    // check input is an image
    // send input to the api 
    //view image in the tag bellow 
    app.models.initModel({ 
      id: Clarifai.COLOR_MODEL, 
    })
      .then(colorModel => {
        return colorModel.predict(this.state.input);
      })
      .then(response => {
        var data = response['outputs'][0]['data']
        console.log(data)
      })
  }

  render() {
    return (
      <div className="App">
        <Particles className="particles" params={particlesOptions} />
        <Navigation />
        <Logo />
        <Rank />
        <ImageLinkForm 
        onInputChange={this.onInputChange} 
        onButtonSubmit={this.onButtonSubmit}/>
        <FaceRecognition imageUrl={this.state.imageUrl}/>

      </div>
    );
  }
}

export default App;
